import React, { useState } from 'react';
import { useInterviewStore } from '..';
import API from '../api';

function SubmitAnswers() {
  const { questions } = useInterviewStore();
  const [answers, setAnswers] = useState([]);
  const [result, setResult] = useState('');

  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = { q: questions[index], a: value };
    setAnswers(newAnswers);
  };

  const handleSubmit = async () => {
    const res = await API.post('/api/interviews/submitAnswers', { answers });
    setResult(res.data.evaluation);
  };

  return (
    <div>
      <h2>Submit Your Answers</h2>
      {questions.map((q, i) => (
        <div key={i}>
          <p>{q}</p>
          <textarea
            value={answers[i]?.a || ''}
            onChange={(e) => handleAnswerChange(i, e.target.value)}
          />
        </div>
      ))}
      <button onClick={handleSubmit}>Submit Answers</button>
      {result && <pre>{result}</pre>}
    </div>
  );
}

export default SubmitAnswers;
