import React, { useState, useEffect } from "react";
import { useInterviewStore } from "../store";

export default function Evaluation() {
  const evaluation = useInterviewStore((s) => s.evaluation);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, [index]);

  if (!evaluation) return null;

  const { evaluations, overallScore } = evaluation;

  const current = evaluations[index];

  return (
    <div className="evaluation-container">
      <h2>Evaluation Result</h2>

      <div className="evaluation-card">
        <p><strong>Question:</strong> {current.question}</p>
        <p><strong>Your Answer:</strong> {current.yourAnswer}</p>
        <p><strong>Feedback:</strong> {current.feedback}</p>
        <p><strong>Score:</strong> {current.score}/10</p>
      </div>

      <div className="evaluation-controls">
        <button onClick={() => setIndex(index - 1)} disabled={index === 0}>Previous</button>
        <button onClick={() => setIndex(index + 1)} disabled={index === evaluations.length - 1}>Next</button>
      </div>

      <p className="evaluation-progress">
        {index + 1} of {evaluations.length}
      </p>

      {index === evaluations.length - 1 && (
        <div className="overall-score">
          <h3>Overall Score: {overallScore}/100</h3>
        </div>
      )}
    </div>
  );
}
