import React, { useState } from "react";
import { useInterviewStore } from "../store";

export default function Evaluation() {
  const { evaluation } = useInterviewStore();
  const [index, setIndex] = useState(0);

  if (!evaluation) return null;

  // Split answers nicely
  const parts = evaluation.split(/Q\d+:/).filter((part) => part.trim() !== "");

  return (
    <div className="evaluation-container">
      <h2 className="evaluation-title">Evaluation Result</h2>
      <p className="evaluation-part">{parts[index]}</p>
      <div className="evaluation-controls">
        <button
          className="btn-primary"
          onClick={() => setIndex((prev) => Math.max(prev - 1, 0))}
          disabled={index <= 0}
        >
          Previous
        </button>
        <button
          className="btn-primary"
          onClick={() => setIndex((prev) => Math.min(prev + 1, parts.length - 1))}
          disabled={index >= parts.length - 1}
        >
          Next
        </button>
      </div>
      <p className="evaluation-progress">
        {index + 1} of {parts.length}
      </p>
    </div>
  );
}
