import { create } from 'zustand';

export const useInterviewStore = create((set) => ({
  parsedResume: '',
  questions: [],
  answers: [],
  currentIndex: 0,
  setParsedResume: (data) => set({ parsedResume: data }),
  setQuestions: (data) => set({ questions: data }),
  addAnswer: (answer) =>
    set((state) => ({
      answers: [...state.answers, answer],
    })),
  nextQuestion: () =>
    set((state) => ({
      currentIndex: state.currentIndex + 1,
    })),
  reset: () =>
    set({
      parsedResume: '',
      questions: [],
      answers: [],
      currentIndex: 0,
    }),
}));
