import React from "react";
import UploadResume from "./components/UploadResume";
import Questions from "./components/Questions";
import SubmitAnswers from "./components/SubmitAnswers";
import Evaluation from "./components/Evaluation";
import { useInterviewStore } from "./store";
import { Toaster } from "react-hot-toast";
import "./App.css";

export default function App() {
  const loading = useInterviewStore((s) => s.loading);
  const loadingText = useInterviewStore((s) => s.loadingText || "Loading...");
  const questions = useInterviewStore((s) => s.questions);

  return (
    <div className="container">
      <h1>Mock Interview</h1>

      {/* ✅ Loading Overlay */}
      {loading && (
        <div className="loading-overlay">
          <div className="spinner"></div>
          <div className="loading-text">{loadingText}</div>
        </div>
      )}

      {/* ✅ Only show Upload when no questions */}
      {!questions.length && <UploadResume />}

      {/* ✅ Only show Questions when questions exist */}
      {questions.length > 0 && (
        <>
          <Questions />
          <SubmitAnswers />
        </>
      )}

      {/* ✅ Always show Evaluation */}
      <Evaluation />

      <Toaster />
    </div>
  );
}
