import { create } from 'zustand';

export const useInterviewStore = create((set) => ({
  parsedResume: '',
  questions: [],
  answers: [],
  currentIndex: 0, // NEW
  setParsedResume: (data) => set({ parsedResume: data }),
  setQuestions: (data) => set({ questions: data }),
  setAnswers: (data) => set({ answers: data }),
  nextQuestion: () => set((state) => ({ currentIndex: state.currentIndex + 1 })),
  addAnswer: (answer) =>
    set((state) => ({
      answers: [...state.answers, answer],
    })),
}));
