import React, { useState } from 'react';
import { useInterviewStore } from '../store/interviewStore';
import API from '../api';
import { toast } from 'react-toastify';

const Questions = () => {
  const { questions, currentIndex, addAnswer, nextQuestion, answers } = useInterviewStore();
  const [answerText, setAnswerText] = useState('');

  if (questions.length === 0) {
    return <p>No questions yet. Upload a resume first.</p>;
  }

  const currentQ = questions[currentIndex];

  const handleNext = () => {
    if (!answerText.trim()) {
      toast.error('Please enter an answer!');
      return;
    }

    addAnswer({ q: currentQ, a: answerText });
    setAnswerText('');

    if (currentIndex + 1 < questions.length) {
      nextQuestion();
    } else {
      toast.success('All questions answered!');
    }
  };

  const handleSubmitAll = async () => {
    try {
      const res = await API.post('/api/interviews/submitAnswers', { answers });
      toast.success('Answers submitted!');
      console.log(res.data.evaluation);
    } catch (err) {
      console.error(err);
      toast.error('Submission failed.');
    }
  };

  return (
    <div>
      <h3>Question {currentIndex + 1}:</h3>
      <p>{currentQ}</p>
      <textarea
        rows="4"
        value={answerText}
        onChange={(e) => setAnswerText(e.target.value)}
        placeholder="Write your answer..."
      />
      <br />
      <button onClick={handleNext}>
        {currentIndex + 1 < questions.length ? 'Next' : 'Finish'}
      </button>

      {currentIndex + 1 >= questions.length && (
        <button onClick={handleSubmitAll} style={{ marginLeft: '1rem' }}>
          Submit Answers
        </button>
      )}
    </div>
  );
};

export default Questions;
