import React, { useState, useEffect } from "react";
import { useInterviewStore } from "../store";
import { useSpeechToText, speak } from "../lib/speak";

export default function Questions() {
  const { questions, currentIndex, addAnswer, nextQuestion } = useInterviewStore();
  const [answer, setAnswer] = useState("");

  const { startListening, stopListening } = useSpeechToText(setAnswer);

  const q = questions[currentIndex];


  if (questions.length === 0) return <p className="placeholder">No questions yet.</p>;
  if (currentIndex >= questions.length) return <p className="placeholder">‚úÖ All questions answered.</p>;

  const handleNext = () => {
    if (!answer.trim()) return; // Prevent empty answers
    addAnswer({ q, a: answer.trim() });
    setAnswer("");
    stopListening(); // Stop mic when moving to next
    nextQuestion();
  };

  return (
    <div className="question-container">
      <div className="question-progress">
        Question {currentIndex + 1} of {questions.length}
      </div>

      <p className="question-text">{q}</p>
      <button onClick={speak(q)}>
          üéôÔ∏è Start Speaking
        </button>

      <textarea
        className="answer-input"
        rows="5"
        placeholder="Type your answer..."
        value={answer}
        onChange={(e) => setAnswer(e.target.value)}
      />

      <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
        <button
          className="btn-primary"
          onClick={handleNext}
        >
          Submit Answer & Next
        </button>

        <button onClick={startListening}>
          üéôÔ∏è Start Speaking
        </button>

        <button onClick={stopListening}>
          üõë Stop Listening
        </button>
      </div>
    </div>
  );
}
